(103, 'acol', '', '<p>\n	虽然小气球的路由功能还没搞定，但是我已经把文章和图片墙的接口改成lua的了~~<img alt="crying" height="96" src="http://xiaoqiqiu.com/ckeditor/plugins/smiley/images/14.gif" title="crying" width="96" /></p>\n<p>\n	<img alt="blush" height="96" src="http://xiaoqiqiu.com/ckeditor/plugins/smiley/images/07.gif" title="blush" width="96" />靠我发现这个代码高亮插件不支持lua，晕倒，果然是小语种，代码贴上</p>\n<p>\n	跟原来的看起来更简洁，好看</p>\n<p>\n	&nbsp;</p>\n<pre class="brush:perl;">\n--[[\n	xiaoqiqiu取图片接口\n\n]]--\n\nlocal Helper        = require &quot;lib.helper&quot;\nlocal Img           = require &quot;mod.img&quot;\nlocal cjson         = require &quot;cjson&quot;\nlocal say           = ngx.say\nlocal print 		= ngx.print\nlocal print_r       = Helper.print_r\nlocal exit          = ngx.exit\nlocal os			= os\nlocal WEB_ROOT 		= &#39;http://xiaoqiqiu.com/&#39;\n\nlocal config 		= {\n	page_listNum = 5,\n	content_num = 18\n}\n\nlocal args = ngx.req.get_uri_args()\n\nngx.header[&quot;Cache-Control&quot;] = &quot;no-cache&quot;;\nngx.header[&quot;Expires&quot;] = &quot;Mon, 20 Jul 1999 23:00:00 GMT&quot;;\nngx.header[&quot;Content-Type&quot;] = &#39;text/html; charset=UTF-8&#39;;\n\nif args[&#39;action&#39;] == &#39;imgList&#39; then\n	local page ,user 	= args[&#39;page&#39;] ,args[&#39;user&#39;]\n	local rows 			= args[&#39;rows&#39;] or config[&#39;content_num&#39;]\n	local callback 		= args[&#39;callback&#39;] or &#39;&#39;\n\n	local Img = Img:new()\n	local data = Img:get_data(&#39;acol&#39; , page ,rows)\n\n	Img:close_db()\n	print( callback ..&#39;(&#39;.. cjson.encode( data ) ..&#39;)&#39; )\n    exit(ngx.HTTP_OK);\n\nend\n\nexit(ngx.HTTP_OK)</pre>\n', 0, 1383657768, '113.107.234.109', 0),
(105, 'acol', '', '<p>\n	<img alt="broken heart" height="96" src="http://xiaoqiqiu.com/ckeditor/plugins/smiley/images/19.gif" title="broken heart" width="96" />核心model v1.0版本，持续增加新功能中..</p>\n<p>\n	&nbsp;</p>\n<pre class="brush:php;">\nlocal Helper    = require &quot;lib.helper&quot;\nlocal Config    = require &quot;lib.config&quot;\nlocal ngx       = ngx\nlocal say       = ngx.say\nlocal print_r   = Helper.print_r\n\nlocal setmetatable          = setmetatable\nlocal type                  = type\nlocal pairs                 = pairs\nlocal ipairs                = ipairs\nlocal tostring              = tostring\nlocal tonumber              = tonumber\nlocal next                  = next\nlocal error                 = error\nlocal table                 = table\n-- db setting\n-- private\nlocal db_set = Config.dbConfig()\n\n-- db相关\nlocal mysql = require &quot;resty.mysql&quot;\n\n\nmodule(...)\n\n_VERSION = &#39;1.00&#39;\n\n--[[\n    private function\n]]--\n\n-- where\nlocal function where(where_arr)\n    local i ,where = 1 ,{}\n\n    for k,v in pairs(where_arr) do\n        where[i] = k ..&#39;=&#39;&#39;.. v ..&#39;&#39;&#39;\n        i = i + 1\n    end\n    where = table.concat(where , &#39; AND &#39;)\n    return &quot; WHERE &quot;.. where\nend\n\n--[[\n    public\n]]--\nlocal mt = { __index = _M }\n\n--  constructor\nfunction new(self , ...)\n    --local db, err = mysql:new()\n\n    return setmetatable({_connDb = nil}, mt)\nend\n\n\n\n-- db连接\nfunction conn_db(self)\n    if not self._db then\n        self._db, self._err = mysql:new()\n    end\n\n    -- 保证只conn一次\n    if self._connDb then\n        return\n    end\n\n    if not self._db then\n       ngx.say(&quot;failed to instantiate mysql: &quot;, self._err)\n       return\n    end\n\n    self._db:set_timeout(1000) -- 1 sec\n\n    local ok, err, errno, sqlstate = self._db:connect{\n                host = db_set[&#39;host&#39;],\n                port = db_set[&#39;port&#39;],\n                database = db_set[&#39;db_name&#39;],\n                user = db_set[&#39;user&#39;],\n                password = db_set[&#39;pass&#39;],\n                max_packet_size = 1024 * 1024 }\n\n    --  set names要在connect之后\n    local charset = db_set[&#39;charset&#39;] or &#39;utf8&#39;;\n    self._db:query(&quot;SET NAMES &quot; .. charset);\n\n    if not ok then\n        ngx.say(&quot;failed to connect: &quot;, err, &quot;: &quot;, errno, &quot; &quot;, sqlstate)\n        return\n    end\n\n    --  正在连接的库\n    self._connDb = db_set[&#39;db_name&#39;]\n    --ngx.say(&#39;yes&#39;);\n    --return self._db\nend\n\n--get one\nfunction get_one(self ,sql)\n    self:conn_db()\n\n    local rs = self._db:query(sql)\n\n    return rs[1]\nend\n\n--get all\nfunction get_all(self ,sql)\n    self:conn_db()\n\n    local rs = self._db:query(sql)\n    return rs\nend\n\n--insert\nfunction insert(self , insert_data ,table_name)\n    self:conn_db()\n    \n    local i ,col ,val ,sql = 1 ,{} ,{} ,&#39;&#39;\n\n    for k,v in pairs(insert_data) do\n        col[i] = &#39;`&#39;.. k ..&#39;`&#39;\n        val[i] = &#39;&#39;&#39;.. v ..&#39;&#39;&#39;\n        i = i + 1\n    end\n\n    col = table.concat(col , &#39;,&#39;)\n    val = table.concat(val , &#39;,&#39;)\n\n    --ngx.say(col)\n    sql = &#39;INSERT INTO &#39;.. table_name ..&#39; ( &#39;.. col ..&#39; ) VALUES ( &#39;.. val ..&#39; )&#39;\n\n    local res, err, errno, sqlstate = self._db:query(sql)\n    return res\n\nend\n\n--update\nfunction update(self , update_data ,where_arr ,table_name)\n    self:conn_db()\n    local sql ,update_str;\n    \n    if type(update_data) == &#39;table&#39; then\n        local i ,update_arr ,sql = 1 ,{} ,&#39;&#39;\n\n        for k,v in pairs(update_data) do\n            update_arr[i] = &#39;`&#39;.. k ..&#39;`=&#39;&#39;.. v ..&#39;&#39;&#39;\n            i = i + 1\n        end\n\n        update_str = table.concat(update_arr , &#39;,&#39;)\n    else\n        update_str = tostring(update_data)\n    end\n\n    --ngx.say(col)\n    sql = &#39;UPDATE `&#39;.. table_name ..&#39;` SET &#39;.. update_str .. where(where_arr)\n\n    local res, err, errno, sqlstate = self._db:query(sql)\n    return res\n\nend\n\n--db close\nfunction close(self)\n    if self._db then\n        self._db:close()\n    end\nend\n\n\n-- 原话：to prevent use of casual module global variables\n-- 这个可以防止new出来的实例table产生全局变量，所以要注意table实体里的参数都要是局部变量，否则就报error\nsetmetatable(_M, {\n    __newindex = function (table, key, val)\n        ngx.log(ngx.EMERG ,&#39;--attempt to write to undeclared variable &quot;&#39; .. key .. &#39;&quot; val:&quot;&#39;.. val ..&#39;&quot; in &#39; .. table._NAME);\n    end\n})</pre>\n', 0, 1383658220, '113.107.234.109', 1),
